// Process incoming claim data for Bupa
const claimData = $input.first().json.body;

// Extract and validate required fields
const processedData = {
  claimId: claimData.claim_id || `BPA-${Date.now()}`,
  provider: {
    name: claimData.provider?.name || "Al Hayat Hospital",
    code: claimData.provider?.code || "AH001",
    branch: claimData.provider?.branch || "MainRiyadh"
  },
  patient: {
    member_id: claimData.patient?.member_id,
    name: claimData.patient?.name,
    national_id: claimData.patient?.national_id
  },
  claim_details: {
    service_date: claimData.claim_details?.service_date,
    total_amount: claimData.claim_details?.total_amount,
    currency: claimData.claim_details?.currency || "SAR",
    diagnosis_codes: claimData.claim_details?.diagnosis_codes || [],
    procedure_codes: claimData.claim_details?.procedure_codes || []
  }
};

// Validation
const errors = [];
if (!processedData.patient.member_id) errors.push("Missing member ID");
if (!processedData.claim_details.service_date) errors.push("Missing service date");
if (!processedData.claim_details.total_amount) errors.push("Missing total amount");

return {
  data: processedData,
  valid: errors.length === 0,
  errors: errors,
  timestamp: new Date().toISOString()
};

// Handle validation errors
const errors = $input.first().json.errors;
const errorMessage = {
  success: false,
  error: 'Validation failed',
  details: errors,
  timestamp: new Date().toISOString()
};

return errorMessage;
