// Process incoming claim data for GlobeMed
const claimData = $input.first().json.body;

// Extract and validate required fields for GlobeMed format
const processedData = {
  claimId: claimData.claimId || `GLB-${Date.now()}`,
  batchNumber: claimData.batchNumber || `GLB-BATCH-${Date.now()}`,
  providerInfo: {
    providerCode: claimData.providerInfo?.providerCode || "AH001",
    providerName: claimData.providerInfo?.providerName || "Al Hayat Hospital",
    branch: claimData.providerInfo?.branch || "MainRiyadh",
    contactPerson: claimData.providerInfo?.contactPerson || "Billing Department",
    phone: claimData.providerInfo?.phone || "+966-11-456-7890",
    email: claimData.providerInfo?.email || "billing@alhayat.example"
  },
  subscriberInfo: {
    memberId: claimData.subscriberInfo?.memberId,
    memberName: claimData.subscriberInfo?.memberName,
    nationalId: claimData.subscriberInfo?.nationalId,
    gender: claimData.subscriberInfo?.gender,
    dateOfBirth: claimData.subscriberInfo?.dateOfBirth,
    relationship: claimData.subscriberInfo?.relationship || "self",
    policyNumber: claimData.subscriberInfo?.policyNumber
  },
  claimDetails: {
    serviceType: claimData.claimDetails?.serviceType || "outpatient",
    serviceDate: claimData.claimDetails?.serviceDate,
    totalAmount: claimData.claimDetails?.totalAmount,
    currency: claimData.claimDetails?.currency || "SAR",
    providerFees: claimData.claimDetails?.providerFees || claimData.claimDetails?.totalAmount,
    patientResponsibility: claimData.claimDetails?.patientResponsibility || 0,
    insurancePayment: claimData.claimDetails?.insurancePayment || 0
  },
  diagnosisInformation: claimData.diagnosisInformation || [],
  procedureInformation: claimData.procedureInformation || [],
  medicationDetails: claimData.medicationDetails || [],
  authorizationInfo: {
    preAuthorizationRequired: claimData.authorizationInfo?.preAuthorizationRequired || false,
    authorizationNumber: claimData.authorizationInfo?.authorizationNumber,
    authorizationDate: claimData.authorizationInfo?.authorizationDate,
    validFrom: claimData.authorizationInfo?.validFrom,
    validTo: claimData.authorizationInfo?.validTo
  }
};

// Validation for GlobeMed specific requirements
const errors = [];
if (!processedData.subscriberInfo.memberId) errors.push("Missing member ID");
if (!processedData.subscriberInfo.memberName) errors.push("Missing member name");
if (!processedData.claimDetails.serviceDate) errors.push("Missing service date");
if (!processedData.claimDetails.totalAmount) errors.push("Missing total amount");
if (!processedData.providerInfo.providerCode) errors.push("Missing provider code");

// Check for required procedure codes for GlobeMed
if (processedData.procedureInformation.length === 0) {
  errors.push("At least one procedure is required");
}

return {
  data: processedData,
  valid: errors.length === 0,
  errors: errors,
  timestamp: new Date().toISOString()
};

// Get current timestamp
const currentTime = new Date().toISOString();

// Prepare success response with GlobeMed specific details
const response = {
  success: true,
  message: 'Claim submitted to GlobeMed successfully',
  payer: 'GlobeMed Saudi Arabia',
  submission_data: $input.first().json,
  next_steps: [
    'Monitor claim status in GlobeMed portal',
    'Check for any rejection reports',
    'Prepare for potential resubmission if needed'
  ],
  timestamp: currentTime
};

return response;

// Handle validation errors with GlobeMed specific context
const errors = $input.first().json.errors;
const claimData = $input.first().json.data;

const errorResponse = {
  success: false,
  error: 'GlobeMed validation failed',
  payer: 'GlobeMed Saudi Arabia',
  errors: errors,
  required_fields: [
    'memberId - Patient member ID',
    'memberName - Patient full name',
    'serviceDate - Date of service',
    'totalAmount - Total claim amount',
    'providerCode - Provider code (AH001)',
    'procedureInformation - At least one procedure'
  ],
  provided_data_summary: {
    claimId: claimData.claimId,
    memberId: claimData.subscriberInfo?.memberId,
    serviceDate: claimData.claimDetails?.serviceDate,
    totalAmount: claimData.claimDetails?.totalAmount,
    providerCode: claimData.providerInfo?.providerCode,
    procedures_count: claimData.procedureInformation?.length || 0
  },
  timestamp: new Date().toISOString()
};

return errorResponse;
